package pratice.tutorial.javaclass;

public class IfElseBlock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 18;

        if(age >= 18) {
            System.out.println("1Voting eligible");
        } 
        else if(age == 18) {
            System.out.println("2Voting eligible");
        }
        else {
            System.out.println("3Voting not eligible");
        }
	}

}
